import java.math.BigInteger;

public class Sqr {

    public static void main(String[] args) {
        int t = 1000000;  // guess
        BigInteger b = new BigInteger("2");
        BigInteger p = new BigInteger("");
        BigInteger q = new BigInteger("");
        BigInteger expected = new BigInteger("");
        BigInteger n = p.multiply(q);
        BigInteger r;
        BigInteger exp = (new BigInteger("1")).shiftLeft(t);

        long begin = System.nanoTime();
        r = b.modPow(exp, n);
        long end = System.nanoTime();

        assert r.equals(expected) : "Unequal value";
        double elapsed_secs = (double) (end - begin) / (1000000000);
        System.out.println("Squarings per second (Java): " + ((double) t / elapsed_secs));
    }

}
