#include <iostream>
#include <ctime>
#include <cassert>
#include <gmpxx.h>
using namespace std;

int main() {
  int t = 1000000;  // guess
  mpz_class b = 2;
  mpz_class p = mpz_class("", 10);
  mpz_class q = mpz_class("", 10);
  mpz_class expected = mpz_class("");
  mpz_class n = p * q;
  mpz_class r;
  mpz_class exp = mpz_class(1) << t;

  clock_t begin = clock();
  mpz_powm(r.get_mpz_t(), b.get_mpz_t(), exp.get_mpz_t(), n.get_mpz_t());
  clock_t end = clock();

  assert(r == expected);
  double elapsed_secs = double(end - begin) / CLOCKS_PER_SEC;
  cout << "Squarings per second (C++ + GMP): " << (double) t / elapsed_secs << "\n";
}
